mapper.select:
select
SITUATION_ID as "situationId"
, SALES_AREA_CODE as "salesAreaCode"
, SALES_AREA_NAME as "salesAreaName"
, SALES_VILLAGE_CODE as "salesVillageCode"
, SALES_VILLAGE_NAME as "salesVillageName"
, BUYER_ID as "buyerId"
, BUYER_SAP_CODE as "buyerSapCode"
, BUYER_NAME as "buyerName"
, SELLER_ID as "sellerId"
, SELLER_NAME as "sellerName"
, BALANCE as "balance"
, USE_AMOUNT as "useAmount"
, AVAILABLE_BALANCE as "availableBalance"
, BUYER_CODE as "buyerCode"
, CATAGORY_NAME as "catagoryName"
, CATAGORY_CODE as "catagoryCode"
, BU_NAME as "buName"
, BU_CODE as "buCode"
, CREATE_PERSON as "createPerson"
, CREATE_DATE as "createDate"
, MODI_PERSON as "modiPerson"
, MODI_DATE as "modiDate"
, DELETE_PERSON as "deletePerson"
, DELETE_DATE as "deleteDate"
, EXTEND1 as "extend1"
, EXTEND2 as "extend2"
, EXTEND3 as "extend3"
from t_fa_account_situation

    and SITUATION_ID=#{situationId}
    and SALES_AREA_CODE=#{salesAreaCode}
    and SALES_AREA_NAME=#{salesAreaName}
    and SALES_VILLAGE_CODE=#{salesVillageCode}
    and SALES_VILLAGE_NAME=#{salesVillageName}
    and BUYER_ID=#{buyerId}
    and BUYER_SAP_CODE LIKE CONCAT('%',#{buyerSapCode},'%')

    and BUYER_NAME LIKE CONCAT('%',#{buyerName},'%')
    and SELLER_ID=#{sellerId}
    and SELLER_NAME=#{sellerName}
    and BALANCE=#{balance}
    and USE_AMOUNT=#{useAmount}
    and AVAILABLE_BALANCE=#{availableBalance}
    and BUYER_CODE LIKE CONCAT('%',#{buyerCode},'%')
    and CATAGORY_NAME=#{catagoryName}
    and CATAGORY_CODE=#{catagoryCode}
    and BU_NAME=#{buName}
    and BU_CODE=#{buCode}
    and CREATE_PERSON=#{createPerson}
    and CREATE_DATE=#{createDate}
    and MODI_PERSON=#{modiPerson}
    and MODI_DATE=#{modiDate}
    and DELETE_PERSON=#{deletePerson}
    and DELETE_DATE=#{deleteDate}
    and EXTEND1=#{extend1}
    and EXTEND2=#{extend2}
    and EXTEND3=#{extend3}
